INSERT INTO `products` (`id`, `category_id`, `name`, `model`, `brand`, `length`, `size`, `image`, `description`, `quantity`, `price`, `promoPrice`, `featured`) VALUES
	(16, 1, 'Харпун Apnea Rebel Open', 'Rebel Open', 'Apnea', 90, NULL, '5da8365004b3b3d1bae190c8070fce8c.jpeg', 'Харпунът Apnea Rebel Open е оборудван с отворена глава, което облекчава процеса на зареждане, особено при дългите харпуни, предоставя чиста мерна линия и прави изстрела по-безшумен. Харпунът е с тръба със стандартен диаметър 28мм., по цялата дължина на която е интегриран водач, придаващ на харпуна необходимата точност при изстрел. При късите модели до 90 см водачът е пластмасов, а при дългите е екструдиран на самата тръба. \r\nЕргономичната гумирана ръкохватка може лесно да бъде заменена със стандартна, което облекчава левичарите. Една от особеностите на дръжката на Apnea Rebel Open е възможността за регулиране на хода на спусъка, което е незаменим плюс при стрелба с ръкавици с различна дебелина. Дръжката е снабдена и с дълга пета за по-лесно зареждане. Реверсният (обратен) спусък предлага изключително тиха работа. Реверсната технология позволява около 10см допълнителна мощ при зареждане.\r\n\r\nСилата на изстрела се гарантира от два кръгли двукомпонентни ластика Primeline с диаметър 16мм. Жълъдът е шарнирен с пластмасови втулки. В комплекта влиза 6.3 мм. стрела от неръждаема стомана 17-4 PH със стандартни засечки. Стандартно харпунът се предлага с основа за макара тип "лястовича опашка".', 20, 200.00, NULL, 0),
	(17, 1, 'Rob Allen Tuna Railgun', 'Tuna Railgun', 'Rob Allen', 110, NULL, 'ccecea8a4a00bfae9f4086bd2f41fc1f.jpeg', 'Алуминиевите рейлгъни на Rob Allen са се доказали като най-здравите харпуни на пазара, способни да отнесат и най-грубото отношение. Интегрираният цял водач се подравнява със профила на спусъковия механизъм и главата. интегрираният водач е част от профила на тръбата, като по този начин се постига по-голяма здравина и се намалява камшичния ефект. Това прави тези харпуни убийствено точни. \r\n\r\nТръбата е изработена от алуминий с авиационни качества с дебелина на стената 1.45mm, а спусъковият механизъм е изработен от заздравена пластмаса. Харпунът е оборудван полу-отворена глава, и 7.5 mm стрела с две загладени засечки от неръждаема стомана 316.\r\n\r\nХарпуните Rob Allen Tuna Vecta Aluminium се предлагат в дължини 110cm и 120cm\r\n\r\nСнабдени са с два цели 16mm ластик и новата подобрена дръжка Vectа 2.', 10, 340.00, NULL, 1),
	(18, 1, 'Epsealon харпун EXIUM G2', 'EXIUM G2', 'Epsealon', 75, NULL, 'e3948633482edef2e88f0e4b733b149b.jpeg', 'Epsealon представя втората версия на ултра популярния си модел харпуни EXIUM, предлагащи изключителна маневреност благодарение на иновативната конструкция тип "биплан".\r\n\r\nХарактеристики:\r\n\r\nАлуминиево тяло от две овоидни тръби (патентована система), комбиниращо латерална хидродинамика с висока устойчивост на огъване при натоварване с ластици.\r\nЕргономична дръжка разработена с идеален ъгъл на ръкохватката спрямо оста на харпуна.\r\nДръжката и петата са покрити с противоплъзгащо термо-еластомерно покритие.\r\nРегулирането на чувствителността на спусъка и предпазителя са комбинирани в един лост.\r\nМногопрофилна глава G2 (патентована система).\r\nОтделяемо мостче за придържане на стрелата.\r\nЛастици: O 18 mm\r\nСтрела: O 6,0 mm', 30, 210.00, NULL, 1),
	(19, 1, 'Beuchat харпун Mundial Competition', 'Mundial Competition', 'Beuchat', 90, NULL, '735c67027fd0419db99068b7891b5cf1.jpeg', 'Mundial Competition може да бъде описан като високо ниво ластичен харпун за начинаещи.\r\n\r\nТехнически характеристики:\r\n\r\nАлуминиева тръба със стандартен диаметър 28mm\r\nДръжка Mundial\r\nТаитянска стрела 6.5mm със стандартни засечки\r\nЗатворена глава\r\nЧифт 16mm ластици\r\nШарнирен жълъд\r\nПлетена корда 2mm', 8, 400.00, 320.00, 1),
	(20, 1, 'Freesub Golden Horn Cabrio Brown Camo', 'Golden Horn Cabrio Brown Camo', 'Freesub', 90, NULL, '50688f3fdd97cdf36d11f176a0aaf048.jpeg', 'Харпунът Freesub Golden Horn Cabrio Brown Camo е разработен с мисълта за по-напредналите харпунджии, изискващи максимума от оръжието. \r\nЕкструдираният интегриран водач на стрелата предоставя максимална точност на изстрела. Конструкцията на водача допринася и за увеличаване на здравината на 28-милиметровото тяло като отстранява възможността за огъването му при натоварване с мощни ластици и елиминира ефекта на вибриране на стрелата при изстрел. \r\nТози харпун се предлага с 6.5мм Sandvik стрела от неръждаема стомана с пинове тип "бодил на роза" и 20мм ластик Dunlop с дайнима жълъд на метални втулки. Дръжката предлага изключително тих патентован метален реверсен спусъков механизъм, интуитивен предпазител и гумирана ръкохватка за по-добър захват.  \r\nХарпунът е изцяло камуфлиран в кафяво и перфектно се слива с околната среда при подводен риболов от засада.\r\n\r\nТехнически характеристики:\r\n\r\n    Уникален кафяв камуфлаж\r\n    28mm алуминиево тяло с екструдиран цял водач;\r\n    5-степенно уплътняване на тялото\r\n    Ергономична дръжка от усилено фибростъкло 6.6мм\r\n    Ръкохватка от специален суров материал за предпазване от приплъзване\r\n    Отворена глава усилено фибростъкло 6.6мм\r\n    20mm цял ластик Dunlop\r\n    Dyneema жълъд с хромирани втулки\r\n    Патентован тих двукомпонентен реверсен спусъков механизъм\r\n    Стрела Sandvik 6.50мм, неръждаема стомана" пинове тип "бодил на роза"\r\n    1.2mm монофил и кримпове\r\n    Амортисьор за линията', 10, 380.00, 300.00, 1),
	(21, 1, 'Beuchat ролер харпун Marlin Carbone Revo-Concept', 'Marlin Carbone Revo-Concept', 'Beuchat', 95, NULL, '18e5a8b33512cedd4b814c0d300cb244.jpeg', 'Ролерът Beuchat Marlin Carbone Revo-Concept предлага патентованата от Beuchat иновация включваща ролер дизайн на главата и система от ластици с обиране от долната страна на тялото. Тази система осигурява водене на ластика по цялото протежение на тялото на харпуна за подобряване на точността и повишаване на общата мощност на изстрела. По този начин се постига значително по-висока скорост на стрелата. Тази концепция значително намалява и отката в момента на изстрела.\r\nКарбоновата версия комбинира изключителната производителност на Revo Concept с лекотата и здравината на карбоновото тяло.\r\n\r\nТехнически характеристики:\r\n\r\nКарбоново тяло с интегриран цял водач за повишаване на точността\r\nДръжка Marlin с основа за монтаж на макара и възможност за обръщане на куката за освобождаване на линията за левичари\r\nРолки REVO CONCEPT на съчмени лагери с бърза QRS система за захващане на стрелата и титаниеви обтекатели\r\nМного здрава 7мм стрела SUPER STEEL с пръстен и контра Off Shore\r\nСистема от 17mm ластици MEGATONE със Spectra® линия\r\nЧерен монофил 1.6mm\r\nЧервен силиконов амортисьор Offshore\r\nСкорост на стрелата при главата: 42 м/сек.', 5, 840.00, NULL, 1),
	(22, 1, 'EPSEALON ЛАСТИЧЕН ХАРПУН COBRA CARBON', 'COBRA Carbon', 'Epsealon', 85, NULL, 'ce1864413c3b5253dc809448313a18aa.jpeg', 'Пригответе се за нещо уникално. Трябва да видите този харпун за да се убедите в това, какво красиво оръжие е новият Epsealon COBRA Carbon.\r\n\r\n\r\n\r\n\r\nКакто и при новия Spartan, моноблок тялото на харпуна е изработено от 100% 3K карбон с диаметър 30mm, дебелина на стената 2mm, предоставящо 200gr/m? и интегриран цял водач. Това тяло обаче предлага две секции тип-сепия, които може да видите само в най-високия клас харпуни ръчна изработка. Това придава на тялото несравнима маневреност и стабилност без откат. Перфектното позициониране на ластиците също допринася за повишаване на точността на изстрела.Петата е интегрирана със спусъка, изработен изцяло от неръждаема стомана 316L. Харпунът включва в комплекта и 30m стоманена Echo макара.\r\n\r\nТяло от 100% Carbon 3K с диаметър 30мм. и дебелина на стената 2mm, 200gr/m?, с интегриран цял водач;\r\nХидродинамична форма с две секции с форма на сепия за несравнима маневреност и стабилност;\r\nАрхитектура с повишена здравина за по-малко огъване, за да се елиминира влиянието на изкривяването на тялото и да се запази точността;\r\nСпусъковият механизъм е изцяло изработен от неръждаема стомана 316L и е едно цяло с петата за зареждане;\r\nЕргономичната и здрава дръжка е с наклон, който дава перфектна чиста мерна линия към целта;\r\nГлавата е отворена, с подвижно мостче и два отвора за бързи кръгли ластици;\r\nГлавата и дръжката са изработени от Nylon 6, подсилен с 30% фирбостъкло за повече здравина;\r\nХарпунът идва със Sandvik стоманена стрела 6.5 мм с мини шарк-финки;\r\nДва ластика Primeline FireStorm 16mm с дайнема жълъди;\r\nХарпунът се предлага стандартно с 30m стоманена Echo макара бънджи, монофилна линия и кримпи.', 10, 310.00, NULL, 0),
	(23, 10, 'SEAC пневматичен харпун ASSO с регулиране', 'ASSO', 'SEAC', 80, NULL, 'ef82bf6a78f99a49d351d9555b0bd7f4.jpeg', 'Пневматичният харпун Asso е един от най-популярните модели на харпунджийския пазар днес. Тази модификация предлага бърз регулатор на мощността, което ви дава възможност за моментално намаляване на мощността на изстрела с 50%. Това превръща ASSO в многопрофилен харпун, адаптиращ се към различните стилове и техники на подводен риболов.\r\n\r\n• Тяло: алуминиев цилиндър с диаметър 40 mm. \r\n• Глава: глава от лека сплав с по-голееми отвори за оптимизиране на изхвърлянето на водата. \r\n• Дръжка: eергтономична и функционална от един материал.\r\n• Камера: диаметърът 13 mm позволява използване на 8 mm стрели. \r\n• Характеристики: Регулатор на мощността до 50%. Широка гама дължини.\r\n• В комплекта: харпун, стрела, инжектор, помпа, ръкохватка за зареждане, въже.', 6, 420.00, NULL, 0),
	(24, 13, 'Костюм Freesub Hunter 7мм Seal Green', 'Freesub', 'Freesub', NULL, 'S/M/L/XL/XXL', '2be378ea294c4b5bbfe0d7ac6bbd80ea.jpeg', 'Неопреновият костюм за подводен риболов Freesub Hunter е произведен от 100% CR (Chloroprene Rubber) неопренов материал. Костюмът предлага комбинация от риза и панталон с висока талия с анатомична кройка, което позволява леко и незатруднено раздишване.\r\n\r\nКостюмът е гол от вътрешната страна (Open cell), а от външната е защитен с камуфлажно жарсе. анатомичната кройка на врата предлага допълнителен комфорт. Както лактите, така и колената са защитени от протектори подсилени с шевове. Същото се отнася и за подложката за зареждане.\r\n\r\nКостюмът за подводен риболов Freesub Hunter предлага най-доброто съотношение цена/качество на пазара.  \r\n\r\n\r\n    100% CR Neoprene (Nam Liong, Taiwan)\r\n    Opencell отвътре, жарсе отвън\r\n    Комбинация риза с боне и панталон с висока талия\r\n    Анатомична кройка\r\n    Анатомична кройка на врата\r\n    Подсилена с шев подложка за зареждане\r\n    Подсилени с шев налакътники и наколенки\r\n    Всички части са подлепени и подшити', 20, 400.00, NULL, 0),
